
var amqp = require('../index.js');
console.log(amqp);

var amqp_object = null;
amqp.factory({
		host : ['172.24.0.21', '172.24.0.22', 'rabbitmq-03.nimbus'],
		queues : { mongo_write_errors: { prefetch_count : 100, on_message: got_message, durable:true, autoDelete:false}}
	},
	function(err, a){
		if (err){
			console.log(err);
		}
		amqp_object = a;
		console.log("Amqp ready");
	}
);

var count=0;

function got_message(msg, headers, deliveryInfo, message){
	console.log(msg);
	message.acknowledge();
}


