
var amqp = require('../index.js');

function log_on_message()
{
	console.log("got message?");
}

amqp.factory({
	//host: 'rabbitmq-03.devel.nimbus',
	queues : { test1: {  prefetch_count : 5, on_message: log_on_message} } },
	function (err, amqp){
		console.log("Publish test1");
		amqp.publish('test1', {a:1}, {deliveryMode:1}, function(err){
			console.log("Callback called, ", err);
		});
	}
);



